        ZUSDC(syntheticStablecoin).mint(address(this), _valueUSDC);
        // Swap zUSDC for USDC
        ICurveMetaPool(curveStablePoolAddress).safeSwap(
            _valueUSDC,
            _maxMarketMovement,
            synthethicStablecoinIndex,
            defaultStablecoinIndex
        );
        // Call deposit function
        _depositFullService(
            _pid,
            _account,
            _valueUSDC,
            _weeksCommitted,
            _vaultEnteredAt,
            _maxMarketMovement
        );
    }

